import VueRouter from "vue-router";
import Vue from "vue";


//1.使用插件
Vue.use(VueRouter);

//2.编写路由
const routes = [
    // {
    //     name:"Home",
    //     path:"/home",
    //     component:
    // },
]

//3.创建路由实例
const router = new VueRouter({
    routes,
    mode:"history"
})

//4.暴露
export default router;
